!(function(e) {
    "use strict";
    e('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function() {
            if (
                location.pathname.replace(/^\//, "") ==
                this.pathname.replace(/^\//, "") &&
                location.hostname == this.hostname
            ) {
                var a = e(this.hash);
                if ((a = a.length ? a : e("[name=" + this.hash.slice(1) + "]")).length)
                    return (
                        e("html, body").animate({ scrollTop: a.offset().top - 70 },
                            1e3,
                            "easeInOutExpo"
                        ), !1
                    );
            }
        }),
        e(".js-scroll-trigger").click(function() {
            e(".navbar-collapse").collapse("hide");
        }),
        e("body").scrollspy({ target: "#mainNav", offset: 100 });

    function a() {
        if (100 < e("#mainNav").offset().top) {
            e("#mainNav").addClass("navbar-shrink");
            var el = document.getElementById('LogoImg');
            //el.style.visibility = "visible";
            el.classList.remove("d-lg-none");
            el.classList.remove("d-sm-block");

            document.getElementById('LogoText').style.visibility = "hidden";
        } else {
            e("#mainNav").removeClass("navbar-shrink");
            var el = document.getElementById('LogoImg');
            // el.style.visibility = "hidden";
            el.classList.add("d-lg-none");
            el.classList.add("d-sm-block");

            document.getElementById('LogoText').style.visibility = "visible";
        }
    }
    a(), e(window).scroll(a);
})(jQuery);